mapper.select:

		SELECT 
		    name, h.store_id,h.htdl_id, dc_rate,start_tm,end_tm,lmt_pnum,intro,bef_price,ddct,cur_pnum,stus_cd,brch,htdl_img,h.regdate,h.updatedate,last_pnum,htdl_lmt_pnum,rsvd_rate
		FROM 
		(
            SELECT rownum rn,name, store_id,htdl_id, dc_rate,start_tm,end_tm,lmt_pnum,intro,bef_price,ddct,cur_pnum,stus_cd,brch,htdl_img,regdate,updatedate
		    /*+ INDEX_DESC(tbl_htdl tbl_htdl_pk)*/
		    FROM
		    (
		    SELECT name, store_id,htdl_id, dc_rate,start_tm,end_tm,lmt_pnum,intro,bef_price,ddct,cur_pnum,stus_cd,brch,htdl_img,regdate,updatedate
		    FROM tbl_htdl
		    WHERE  store_id = #{storeId}
		    ORDER BY regdate desc
		    )
		    WHERE rownum <= #{cri.pageNum} * #{cri.amount}
        ) h, tbl_htdl_rslt hr
		WHERE rn > (#{cri.pageNum} -1) * #{cri.amount}
        AND h.htdl_id = hr.htdl_id

select COUNT(*)
from tbl_htdl
where store_id = #{storeId}

select count(*)
from tbl_htdl
where to_char(regdate, 'yyyy/MM/dd') = to_char(sysdate, 'yyyy/MM/dd') and store_id = #{storeId}

	SELECT h.*, d.*, e.*
      FROM tbl_htdl h, tbl_htdl_dtls d, tbl_store_eval e
	 WHERE h.htdl_id = d.htdl_id and h.store_id = e.store_id and h.htdl_id = #{htdlId}

mapper.update:
update tbl_htdl_dtls
set menu_Name = #{menuName}, menu_Price = #{menuPrice}, updatedate = sysdate +9/24
where htdl_Id = #{htdlId} and htdl_Seq = #{htdlSeq}

mapper.select:
select stus_cd
from tbl_htdl
where htdl_id = #{htdlId}

SELECT h.htdl_id, h.name, h.store_id, h.htdl_img, h.dc_rate, h.start_tm, h.end_tm, h.lmt_pnum, h.intro ,h.bef_price, h.ddct, h.cur_pnum,stus_cd, h.brch, h.set_name, d.htdl_seq, d.menu_name, e.avg_rating, e.revw_tot_num, e.like_tot_num FROM (select * from ( select /*+ INDEX_DESC(tbl_htdl tbl_htdl_pk)*/ rownum rn, htdl_id, name, store_id, htdl_img, dc_rate, start_tm, end_tm, lmt_pnum, intro, bef_price, ddct, cur_pnum, stus_cd, regdate, updatedate, brch, set_name from tbl_htdl where stus_cd = #{stusCd} and rownum <= #{hCri.pageNum} * #{hCri.amount}
brch like '%'||#{hCri.keyword}||'%'
and to_date(start_tm, 'yyyy/mm/dd HH24:mi:ss') between to_date(#{hCri.startTm}, 'yyyy/mm/dd HH24:mi:ss') and to_date(#{hCri.endTm}, 'yyyy/mm/dd HH24:mi:ss') and to_date(end_tm, 'yyyy/mm/dd HH24:mi:ss') <= to_date(#{hCri.endTm}, 'yyyy/mm/dd HH24:mi:ss')
) where rn > (#{hCri.pageNum} -1) * #{hCri.amount} ) h, tbl_htdl_dtls d, tbl_store_eval e WHERE h.htdl_id = d.htdl_id and h.store_id = e.store_id order by h.htdl_id desc

mapper.update:
update tbl_htdl
set stus_cd = #{stusCd}
where htdl_id = #{htdlId}

update tbl_htdl
set cur_pnum = #{curPnum}
where htdl_id = #{htdlId}
